# Exercise 2 Review: Random + Loop + Conditions
import random
for n in range(10):
    num = random.randint(1, 100)
    if num % 2 == 0:
        print(f"{num} - num is even")
    elif num > 50:
        print(f"{num} - High Odd")
    else:
        print(f"{num}")

# Break - exit the loop completely
for i in range(10):
    if i == 5:
        break  # Stop when i equals 5
    print(i)  # Prints: 0, 1, 2, 3, 4

# Continue - skip to next iteration
for i in range(10):
    if i % 2 == 0:
        continue  # Skip even numbers
    print(i)  # Prints: 1, 3, 5, 7, 9

# Lab example: Sum positive numbers
total = 0
while True:
    num = int(input("Enter a number: "))
    if num < 0:
        break  # Stop if negative
    total += num
print(f"Sum of positive numbers: {total}")

# Creating sets
unique_numbers = {1, 2, 3, 3, 4}  # Duplicates removed: {1, 2, 3, 4}
animals = {'cat', 'dog', 'bird'}
empty_set = set()  # Note: {} creates a dictionary!

# Adding and removing items
animals.add('fish')        # Add item
animals.remove('dog')      # Remove item (error if not found)
animals.discard('mouse')   # Remove item (no error if not found)

# Set operations
set1 = {1, 2, 3, 4}
set2 = {3, 4, 5, 6}
print(set1 & set2)  # Intersection: {3, 4}
print(set1 | set2)  # Union: {1, 2, 3, 4, 5, 6}
print(set1 - set2)  # Difference: {1, 2}
